import { useContext } from 'react';
import { Row, Col, Button, Select } from 'antd';
import { StoreContext } from '../store/index';
import { setBurgerDetail } from '../actions/index';
import Burger from '../pages/Buger';
import burgerBuns from '../json/burgerBuns.json';
import bun from '../images/img-top-buns.png';

const { Option } = Select;

function BurgerDetail() {
     const { state: { burgerDetail: { burgerBun, qty } }, dispatch } = useContext(StoreContext);
    // const onClickMinus = () => {
    //     {burgerBuns}
    // }
    return(
        <div>
            <div className="step-bun">
                <img className="step-bun-img" src={bun} />
            </div>
            <Row className="selectSection">
                <Col span={12}>
                    <div className="bunsBox">
                        <img className="select-classic-buns" src={burgerBuns[0].image} />
                    </div>

                </Col>

                <Col span={12} className="selectSection-selector">
                    {burgerBuns.map(burgerBun => (

                        <div key={burgerBun.id} className="selectSection-selector-right">
                            <Button className="selectBurgerBun" shape="round">
                                {burgerBun.name}
                            </Button>

                            <span className="selectPrice"> ${burgerBun.price}</span>
                            <span className="count">
                                <Select
                                    defaultValue={qty}
                                    value={qty}
                                    className="select-style"
                                    onChange = {val => setBurgerDetail(dispatch, burgerBun.id, val)}
                                >
                                {[...Array(burgerBun.countInStock).keys()].map((x) => (
                                    <Option key={x + 1} value={x + 1}>
                                        {x + 1}
                                    </Option>
                                ))}
                                </Select>

                                {/* <span><Button className="count-minus">
                                    <p className="count-minus-p">-</p>
                                </Button></span>
                                <span className="count-qty">
                                    <div></div>
                                </span>
                                <span><Button className="count-plus">
                                    <p className="count-plus-p" onClick={burgerBun.countInStock+1}>+</p>
                                </Button></span> */}
                            </span>   
                        </div>
                    ))} 
                </Col>
            </Row>
        </div>

    );
}

export default BurgerDetail;
