import { useContext } from 'react';
import { Row, Col, Button } from 'antd';
import { StoreContext } from '../store/index';
import { setBurgerDetail } from '../actions/index';
import Burger from '../pages/Buger';
import burgerBuns from '../json/burgerBuns.json';
import bun from '../images/img-top-buns.png';
$(function() {
    // This button will increment the value
    $('.qtyplus').click(function(e) {
      // Stop acting like a button
      e.preventDefault();
      // Get the field name
      fieldName = $(this).attr('field');
      // Get its current value
      var currentVal = parseInt($('input[name=' + fieldName + ']').val());
      // If is not undefined
      if (!isNaN(currentVal)) {
        // Increment
        $('input[name=' + fieldName + ']').val(currentVal + 1);
      } else {
        // Otherwise put a 0 there
        $('input[name=' + fieldName + ']').val(0);
      }
    });
    // This button will decrement the value till 0
    $(".qtyminus").click(function(e) {
      // Stop acting like a button
      e.preventDefault();
      // Get the field name
      fieldName = $(this).attr('field');
      // Get its current value
      var currentVal = parseInt($('input[name=' + fieldName + ']').val());
      // If it isn't undefined or its greater than 0
      if (!isNaN(currentVal) && currentVal > 0) {
        // Decrement one
        $('input[name=' + fieldName + ']').val(currentVal - 1);
      } else {
        // Otherwise put a 0 there
        $('input[name=' + fieldName + ']').val(0);
      }
    });
  });
function BurgerDetail() {
    // const { state: { burgerDetail: { material, qty } }, dispatch } = useContext(StoreContext);

    return(
        <div>
            <div className="step-bun">
                <img className="step-bun-img" src={bun} />
            </div>
            <Row className="selectSection">
                <Col span={12}>
                    <div className="bunsBox">
                        <img className="select-classic-buns" src={burgerBuns[0].image} />
                    </div>

                </Col>

                <Col span={12} className="selectSection-selector">
                    {burgerBuns.map(burgerBun => (
                        <div key={burgerBun.id}>
                            <Button className="selectBurgerBun" shape="round">
                                {burgerBun.name}
                            </Button>

                            <span className="selectPrice"> ${burgerBun.price}</span>
                            <span className="count">
                                <span>
                                    <input type='button' value='-' class='qtyminus' field='quantity' />
                                </span>
                                <span>
                                    <input type='text' name='quantity' value='0' class='qty' />
                                </span>
                                <span>
                                    <input type='button' value='+' class='qtyplus' field='quantity' />
                                </span>
                            </span>

                        </div>

                    ))}

                </Col>
            </Row>
        </div>

    );
}

export default BurgerDetail;
